mapper.select:
SELECT * 
FROM boardview
WHERE bno=#{bno}

AND m_id=#{m_id}

mapper.delete:
DELETE 
FROM board
WHERE bno=#{bno} and m_no=(SELECT m_no FROM members WHERE m_id=#{m_id});

mapper.update:
UPDATE board 
SET btitle=#{btitle}, bcontent=#{bcontent}
WHERE bno=#{bno}

UPDATE board
SET blike= blike +1
WHERE bno =#{bno}
